"use strict";document.addEventListener("mousemove",function(a){this.querySelectorAll(".layer").forEach(function(e){var n=e.getAttribute("data-depth"),t=(window.innerWidth-a.pageX*n)/100,n=(window.innerHeight-a.pageY*n)/100;e.style.transform="translateX(".concat(t,"px) translateY(").concat(n,"px)")})});var menuIcon=document.getElementById("menu-icon"),barsIcon=document.getElementById("bars-icon"),nav=document.getElementById("nav");menuIcon.addEventListener("click",function(){nav.classList.add("hide"),menuIcon.classList.add("hide"),barsIcon.classList.remove("hide")}),barsIcon.addEventListener("click",function(){nav.classList.remove("hide"),menuIcon.classList.remove("hide"),barsIcon.classList.add("hide")});var hero=gsap.timeline({defaults:{duration:1.5}});hero.from(".hero__animation",{opacity:0,y:40,stagger:.3});var dropdown=gsap.timeline({defaults:{duration:1.8,delay:1}});dropdown.from(".dropdown-animation",{opacity:0,y:-10});var about=gsap.timeline({defaults:{duration:1.2}});about.from(".about-animation",{y:5,repeat:-1,yoyo:!0});